package Lessons;

public class MathOperationsPractice {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println(3 - 48 * 23 + 16 / 10);
		System.out.println(12 % 7);
		System.out.println(Math.sqrt(2));
		System.out.println(Math.pow(7, 42));
		System.out.println(Math.abs(-50));
		System.out.println((-2 + Math.sqrt(8)) * Math.pow(14, 3));
		System.out.println(Math.random()*((10) + 1)); 
		System.out.println(Math.random()*((8000 - 1000) + 1) + 1000); 
		System.out.println(Math.random()*((75 - 25) + 1) + 25); 
		System.out.println(Math.random()*(10));
		System.out.println((int) (Math.random()*((12 - 2) + 1) + 2)); 
		
		System.out.println(Math.abs((Math.random()*((25-15)+1) +15) - Math.random()*((-15+25) + 1) -25));
		
		int seconds = 38800;
		System.out.println(seconds / 60 / 60 / 24);
		
		int miles = 500;
		System.out.println(miles * 1.6);
		
		System.out.println((Math.random()*((150-100)+1) + 100) * 0.45);
		
		
		
	}

}
